/* Optimized slow spinner without center dot */
.spinner {
  position: absolute;
  inset: 0;
  margin: auto;
  width: 1em;
  height: 1em;
  font-size: 28px;
  border-radius: 50%;
  box-shadow:
    0 -1.4em 0 0 #69717d,
    1em -1em 0 0 #69717d,
    1.4em 0 0 0 #69717d,
    1em 1em 0 0 #69717d,
    0 1.4em 0 0 #69717d,
   -1em 1em 0 0 #69717d,
   -1.4em 0 0 0 #69717d,
   -1em -1em 0 0 #69717d;
  background: transparent; /* remove center dot */
  animation: spinner-rotate 3s linear infinite;
}

@keyframes spinner-rotate {
  0%   { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* checkbox.cc */
/* Minimal, clean version — all text white */

.my-form {
  --_clr-primary: #ffffff; /* White text */
  --_clr-hover: #ff7a7a;
  --_clr-checked: #4fa3ff;

  font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Arial, sans-serif;
  color: white;
}

.my-form > div {
  --_clr-current: var(--_clr-primary);

  display: flex;
  align-items: center;
  gap: 0.5rem;
  color: white;
}

.my-form label {
  cursor: pointer;
  color: white !important; /* Force white text */
  font-size: 15px;
  transition: color 150ms ease-in-out;
}

.my-form input[type="checkbox"] {
  appearance: none;
  outline: none;
  width: 1.4rem;
  height: 1.4rem;

  padding: 0.25rem;
  background: transparent;

  border: 2px solid white !important; /* White border */
  border-radius: 4px;

  display: grid;
  place-content: center;
  cursor: pointer;

  transition: border-color 150ms ease-in-out, background-color 150ms ease-in-out;
}

.my-form input[type="checkbox"]::after {
  content: "✔";
  opacity: 0;
  color: var(--_clr-checked);
  font-size: 1rem;
  transition: opacity 150ms ease-in-out;
}

.my-form input[type="checkbox"]:checked::after {
  opacity: 1;
}
